import React, {useEffect,useState} from "react"
import axios from "axios"
import {useNavigate, Link} from "recat-router-dom"


function Login(){

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')

    async function submit(e){
        e.preventDefault();

        try{

            await axios .post("http://localhost:8000/",{
                email,password
            })
        }
        catch(e){
            console.log(e);

        }
    }

    return(
        <div className="login">
            <h1>Login</h1>
            <from action="POST">
                <input type="email" text onChange={(e)=>{setEmail(e.target.value)}} placeholder="Email"/>
                <input type="password" text onChange={(e)=>{setPassword(e.target.value)}} placeholder="Password"/>
                <input type="submit" onClick={submit}/>

            <input type="submit"/>
            </from>

            <br/>
            <p>OR</p>
            <br/>

            <Link to="/signup">Signup Page</Link>

        </div>
    )
}

export default Login
